<!DOCTYPE html><html><head>    <title>PHP-скрт</title></head><body><style>    body {        background-color: black;        color: white;        font-family: Arial, "Helvetica Neue", Helvetica, sans-serif;        font-size: 17px;    }</style><?php$months = array(    "January" => "winter",    "February" => "winter",    "March" => "spring",    "April" => "spring",    "May" => "spring",    "June" => "summer",    "July" => "summer",    "August" => "summer",    "September" => "autumn",    "October" => "autumn",    "November" => "autumn",    "December" => "winter");echo "<h3>Місяці :</h3>";foreach ($months as $month => $season) {    if ($season == "winter") {        echo "<span style='color: blue;'>$month</span><br>";    } elseif ($season == "spring") {        echo "<span style='color: pink;'>$month</span><br>";    } elseif ($season == "summer") {        echo "<span style='color: green;'>$month</span><br>";    } elseif ($season == "autumn") {        echo "<span style='color: yellow;'>$month</span><br>";    }}echo "<h3>Місяці :</h3>";foreach ($months as $month => $season) {    switch ($season) {        case "winter":            echo "<span style='color: blue;'>$month</span><br>";            break;        case "spring":            echo "<span style='color: pink;'>$month</span><br>";            break;        case "summer":            echo "<span style='color: green;'>$month</span><br>";            break;        case "autumn":            echo "<span style='color: yellow;'>$month</span><br>";            break;    }}// Відомості про товари 5 include 'prod.php';echo "<h2>Товари та їх вартість</h2>";echo "<table border='1'>";echo "<tr><th>Назва товару</th><th>Вартість</th></tr>";$totalCost = 0;foreach ($products as $productName => $productCost) {    echo "<tr><td>$productName</td><td>$productCost</td></tr>";    $totalCost += $productCost;}echo "</table>";$averageCost = $totalCost / count($products);echo "<p>Середня вартість товарів: $averageCost</p>";// Відомості про товари з додатковою інформацією 6include 'prod1.php';echo "<h2>Товари та їх вартість</h2>";echo "<table border='1'>";echo "<tr><th>Назва товару</th><th>Ціна</th><th>Кількість</th></tr>";$totalCost = 0;foreach ($products as $productName => $productDetails) {    $price = $productDetails["price"];    $quantity = $productDetails["quantity"];    echo "<tr><td>$productName</td><td>$price</td><td>$quantity</td></tr>";    $totalCost += $price * $quantity;}echo "</table>";$averageCost = $totalCost / count($products);echo "<p>Загальна вартість товарів: $totalCost</p>";echo "<p>Середня вартість товарів: $averageCost</p>";// Створення масиву з випадковими числами 7$sequentialArray = array(); for ($i = 0; $i < 30; $i++) {    $sequentialArray[] = rand(19, 570);}echo "Вміст масиву:";echo "<pre>";print_r($sequentialArray);echo "</pre>";// Знаходження найменшого і найбільшого числа в масиві$minNumber = $sequentialArray[0];$maxNumber = $sequentialArray[0];$count = count($sequentialArray);$index = 1;do {    $currentNumber = $sequentialArray[$index];    if ($currentNumber < $minNumber) {        $minNumber = $currentNumber;    }    if ($currentNumber > $maxNumber) {        $maxNumber = $currentNumber;    }    $index++;} while ($index < $count);// Виведення найменшого і найбільшого числаecho "<p>Найменше число: $minNumber</p>";echo "<p>Найбільше число: $maxNumber</p>";// Пошук числа в масиві 8$searchNumber = 54;$found = false;foreach ($sequentialArray as $index => $value) {    if ($value == $searchNumber) {        echo "<p>Перше входження числа $searchNumber знаходиться на позиції " . ($index + 1) . "</p>";        $found = true;        break;    }}// Повідомлення, якщо елемент не знайденоif (!$found) {    echo "<p>Число $searchNumber не знайдено в масиві.</p>";}// Виведення двоцифрових чисел з масиву 9$index = 0;while ($index < count($sequentialArray)) {    $value = $sequentialArray[$index];    if ($value >= 10 && $value <= 99) {        echo " $value";    }    $index++;    continue;}?></body></html>